let header=document.getElementById("header"),main=document.getElementById("main"),sideNav=document.getElementById("sideNav"),footer=document.getElementById("footer");document.getElementById("graphic").addEventListener("click",()=>{console.log("haha"),header.classList.contains("active")?(header.classList.remove("active"),footer.classList.remove("active")):(header.classList.add("active"),footer.classList.add("active"))}),document.getElementById("exit").addEventListener("click",()=>{header.classList.remove("active"),footer.classList.remove("active")}),document.getElementById("mobile").addEventListener("click",()=>{main.style.marginLeft="25vw",sideNav.classList.add("active")}),document.getElementById("sideClose").addEventListener("click",()=>{main.style.marginLeft="0",sideNav.classList.remove("active")});