let header=document.getElementById("header"),footer=document.getElementById("footer");document.getElementById("graphic").addEventListener("click",()=>{header.classList.contains("active")?(header.classList.remove("active"),footer.classList.remove("active")):(header.classList.add("active"),footer.classList.add("active"))}),document.getElementById("exit").addEventListener("click",()=>{header.classList.remove("active"),footer.classList.remove("active")});